import React from "react";
import I18n from "i18n-js";
import "../../bundles/i18n/ja.js";
import AddUser from "./_entry.jsx";
import UserList from "./_list.jsx";
import Highlighter from "react-highlight-words";

import { Input, Button, Popconfirm, Space, Card } from "antd";
import {
  EditOutlined,
  DeleteOutlined,
  SearchOutlined,
} from "@ant-design/icons";

class User extends React.Component {
  state = {
    users: [],
    userLevels: [],
    searchText: "",
    searchedColumn: "",
    status: "",
    visible: false,
    type: "",
    value: "",
    editable: false,
    editUser: [],
    errorMessage: "",
    duplicate: false,
    error: "",
  };

  formlayout = {
    labelCol: {
      span: 10,
    },
    wrapperCol: {
      span: 6,
    },
  };

  // テーブル列検索処理
  getColumnSearchProps = (dataIndex) => ({
    filterDropdown: ({
      setSelectedKeys,
      selectedKeys,
      confirm,
      clearFilters,
    }) => (
      <div style={{ padding: 8 }}>
        <Input
          ref={(node) => {
            this.searchInput = node;
          }}
          value={selectedKeys[0]}
          onChange={(e) =>
            setSelectedKeys(e.target.value ? [e.target.value] : [])
          }
          onPressEnter={() =>
            this.handleSearch(selectedKeys, confirm, dataIndex)
          }
          style={{ marginBottom: 8, display: "block" }}
        />

        <Space>
          <Button
            type="primary"
            onClick={() => this.handleSearch(selectedKeys, confirm, dataIndex)}
            icon={<SearchOutlined />}
            size="small"
            style={{ width: 90 }}
          >
            {I18n.t("common.search")}
          </Button>

          <Button
            onClick={() => this.handleReset(clearFilters)}
            size="small"
            style={{ width: 90 }}
          >
            {I18n.t("common.reset")}
          </Button>
          <Button
            type="link"
            size="small"
            onClick={() => {
              confirm({ closeDropdown: false });

              this.setState({
                searchText: selectedKeys[0],

                searchedColumn: dataIndex,
              });
            }}
          >
            {I18n.t("common.filter")}
          </Button>
        </Space>
      </div>
    ),
    filterIcon: (filtered) => (
      <SearchOutlined style={{ color: filtered ? "#1890ff" : undefined }} />
    ),

    onFilter: (value, record) =>
      record[dataIndex]
        ? record[dataIndex]
            .toString()
            .toLowerCase()
            .includes(value.toLowerCase())
        : "",

    onFilterDropdownVisibleChange: (visible) => {
      if (visible) {
        setTimeout(() => this.searchInput.select(), 100);
      }
    },
    render: (text) =>
      this.state.searchedColumn === dataIndex ? (
        <Highlighter
          highlightStyle={{ backgroundColor: "#ffc069", padding: 0 }}
          searchWords={[this.state.searchText]}
          autoEscape
          textToHighlight={text ? text.toString() : ""}
        />
      ) : (
        text
      ),
  });

  //テーブル列検索にある検索ボタン処理
  handleSearch = (selectedKeys, confirm, dataIndex) => {
    confirm();

    this.setState({
      searchText: selectedKeys[0],

      searchedColumn: dataIndex,
    });
  };

  //テーブル列検索のリセット処理
  handleReset = (clearFilters) => {
    clearFilters();

    this.setState({ searchText: "" });
  };

  //更新のためデータをformに引っ張り処理
  handleEdit = (record) => {
    this.setState({ editable: true });
    this.state.editUser = {
      key: record.key,
      id: record.id,
      name: record.user_name,
      email: record.email,
      user_level_id: record.user_level_id,
      user_level: record.user_level,
    };
    this.setState(this.state.editUser);
  };

  // 一覧のデータを変更
  getUserData = (data) => {
    data.forEach((user, index) => {
      let userName;
      // ユーザー名はuser_nameとuser_name_lastを合併して表示する
      if (user.user_name != null && user.user_name_last == null) {
        userName = user.user_name;
      } else {
        if (user.user_name == null && user.user_name_last != null) {
          userName = user.user_name_last;
        } else {
          if (user.user_name != null && user.user_name_last != null) {
            userName = user.user_name + " " + user.user_name_last;
          } else {
            userName = " ";
          }
        }
      }
      const newEl = {
        key: user.id,
        id: index + 1,
        name: userName,
        email: user.email,
        user_level: user.user_level,
        user_level_id: user.user_level_id,
      };
      this.setState((prevState) => ({
        users: [...prevState.users, newEl],
      }));
    });
  };

  // メッセージを表示する
  showMessage = (status) => {
    this.setState({
      status: I18n.t("message." + status),
      visible: true,
      type: "error",
      editUser: [],
    });
  };
  //ユーザーテーブルcolumn
  columns = [
    {
      title: I18n.t("user.id"),
      dataIndex: "id",
      key: "id",
      align: "center",
    },
    {
      title: I18n.t("user.name"),
      dataIndex: "name",
      key: "name",
      width: "27%",
      ...this.getColumnSearchProps("name"),
    },

    {
      title: I18n.t("user.email"),
      dataIndex: "email",
      key: "email",
      width: "27%",
      ...this.getColumnSearchProps("email"),
    },
    {
      title: I18n.t("user.user_level"),
      dataIndex: "user_level",
      key: "user_level",
      width: "27%",

      sorter: (a, b) => a.user_level_id - b.user_level_id,
      ...this.getColumnSearchProps("user_level"),
    },
    {
      title: I18n.t("common.action"),
      key: "action",
      align: "center",
      render: (_text, record) => (
        <Space size="middle">
          <a onClick={() => this.handleEdit(record)}>
            <EditOutlined />
          </a>
          <Popconfirm
            title={I18n.t("message.M006")}
            onConfirm={() => this.deleteUser(record.key)}
            okText={I18n.t("common.okText")}
            cancelText={I18n.t("common.cancelText")}
          >
            <a href="#" type="danger">
              <DeleteOutlined />{" "}
            </a>
          </Popconfirm>
        </Space>
      ),
    },
  ];

  //テーブル列検索のリセット処理
  handleReset = (clearFilters) => {
    clearFilters();
    this.setState({ searchText: "" });
  };

  //formに入るデータを消す処理
  doReset = () => {
    document.getElementById("form").reset();
  };

  // アラートメッセージ止める処理
  handleClose = () => {
    this.setState({ visible: false });
  };

  // ユーザーデータをidでデータベースに削除する
  deleteUser = (key) => {
    const url = "/api/v1/user/destroy/" + key;

    fetch(url, {
      method: "PUT",
    })
      .then((data) => {
        if (data.ok) {
          return data.json();
        } else {
          // エラーメッセージを表示する
          // M059: データ削除に失敗しました。
          this.setState({
            status: I18n.t("message.M059"),
            visible: true,
            type: "error",
          });
        }
      })
      .then((data) => {
        if (data.status) {
          // 成功メッセージを表示する
          // M007: 削除しました。
          this.setState({
            status: I18n.t("message.M007"),
            visible: true,
            type: "success",
            users: [],
          });
          // データを順序する
          this.getUserData(data.users);
        } else {
          // エラーメッセージを表示する
          // M059: データ削除に失敗しました。
          this.showMessage(I18n.t("message.M059"), "error");
        }
        this.doReset();
      })
      .catch(() => {
        // エラーメッセージを表示する
        // M059: データ削除に失敗しました。
        this.showMessage("M059");
      });
  };

  //ユーザーデータを登録ためにデータベースに保存する
  onSave = (values) => {
    const url = "api/v1/user/create";
    fetch(url, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(values),
    })
      .then((data) => {
        if (data.ok) {
          return data.json();
        } else {
          // エラーメッセージを表示する
          // M056: データ登録に失敗しました。
          this.setState({
            status: I18n.t("message.M056"),
            visible: true,
            type: "error",
          });
        }
      })
      .then((data) => {
        if (data.duplicate) {
          // エラーメッセージを表示する
          // M002: 既に登録されているメールアドレスです。
          this.setState({
            duplicate: true,
            status: I18n.t("message.M002"),
            visible: true,
            type: "error",
          });
          // フォームにデータを設定する
          this.handleEdit(values);
        } else {
          if (data.status) {
            // 成功メッセージを表示する
            // M004: 登録しました。
            this.setState({
              duplicate: false,
              status: I18n.t("message.M004"),
              visible: true,
              type: "success",
              editUser: [],
              users: [],
            });
            // データを順序する
            this.getUserData(data.users);
          } else {
            // エラーメッセージを表示する
            // M056: データ登録に失敗しました。
            this.setState({
              status: I18n.t("message.M056"),
              visible: true,
              type: "error",
            });
          }
        }
      })
      .catch(() => {
        // エラーメッセージを表示する
        // M056: データ登録に失敗しました。
        this.showMessage("M056");
      });
  };

  //ユーザーデータをidでデータベースに更新
  onUpdate = (values) => {
    const url = "api/v1/user/update/" + values.key;
    fetch(url, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(values),
    })
      .then((data) => {
        if (data.ok) {
          return data.json();
        } else {
          // エラーメッセージを表示する
          // M058: データ更新に失敗しました。
          this.setState({
            status: I18n.t("message.M058"),
            visible: true,
            type: "error",
          });
        }
      })
      .then((data) => {
        if (data.duplicate) {
          // エラーメッセージを表示する
          // M002: 既に登録されているメールアドレスです。
          this.setState({
            duplicate: true,
            status: I18n.t("message.M002"),
            visible: true,
            type: "error",
          });
          // フォームにデータを設定する
          this.handleEdit(values);
        } else {
          if (data.status) {
            // 成功メッセージを表示する
            // M005: 更新しました。
            this.setState({
              duplicate: false,
              errorMessage: "",
              status: I18n.t("message.M005"),
              visible: true,
              type: "success",
              editUser: [],
              users: [],
            });
            // データを順序する
            this.getUserData(data.users);
          } else {
            // エラーメッセージを表示する
            // M058: データ更新に失敗しました。
            this.setState({
              status: I18n.t("message.M058"),
              visible: true,
              type: "error",
            });
          }
        }
      })
      .catch(() => {
        // エラーメッセージを表示する
        // M058: データ更新に失敗しました。
        this.showMessage("M058");
      });
  };

  //_entry.jsxから入るidによって更新か登録か決める
  onFinish = (values) => {
    if (values.key > 0) {
      this.onUpdate(values);
    } else {
      this.onSave(values);
    }
  };
  componentDidMount() {
    this.loadUsers();
  }

  //ユーザーデータを一覧に引っ張り処理
  loadUsers = () => {
    const url = "/api/v1/user/index";
    fetch(url)
      .then((data) => {
        if (data.ok) {
          return data.json();
        } else {
          // エラーメッセージを表示する
          // M060: データ取得に失敗しました。
          this.setState({
            status: I18n.t("message.M060"),
            visible: true,
            type: "error",
          });
        }
      })
      .then((data) => {
        data.users.forEach((user, index) => {
          // ユーザー名はuser_nameとuser_name_lastを合併して表示する
          let userName;
          if (user.user_name != null && user.user_name_last == null) {
            userName = user.user_name;
          } else {
            if (user.user_name == null && user.user_name_last != null) {
              userName = user.user_name_last;
            } else {
              if (user.user_name != null && user.user_name_last != null) {
                userName = user.user_name + " " + user.user_name_last;
              } else {
                userName = " ";
              }
            }
          }

          const newEl = {
            key: user.id,
            id: (index += 1),
            name: userName,
            email: user.email,
            user_level: user.user_level,
            user_level_id: user.user_level_id,
          };
          this.setState((prevState) => ({
            users: [...prevState.users, newEl],
          }));
        });
        data.userlevel.forEach((userLevel) => {
          const newEl2 = {
            key: userLevel.id,
            id: userLevel.id,
            user_level: userLevel.user_level,
          };

          this.setState((prevState) => ({
            userLevels: [...prevState.userLevels, newEl2],
          }));
        });
      })
      .catch(() => {
        // エラーメッセージを表示する
        // M060: データ取得に失敗しました。
        this.showMessage("M060");
      });
  };

  render() {
    return (
      <>
        <div
          className="site-layout-background"
          style={{ padding: 24, minHeight: 360, align: "right" }}
        >
          <Space direction="vertical" style={{ width: "100%" }}>
            <Card>
              <AddUser
                onFinish={this.onFinish}
                onChange={this.handleChange}
                formlayout={this.formlayout}
                userLevels={this.state.userLevels}
                value={this.state.value}
                visible={this.state.visible}
                status={this.state.status}
                type={this.state.type}
                errorMessage={this.state.errorMessage}
                duplicate={this.state.duplicate}
                handleClose={this.handleClose}
                editUser={this.state.editUser}
                editable={this.state.editable}
                error={this.state.error}
              />
            </Card>
            <UserList users={this.state.users} columns={this.columns} />
          </Space>
        </div>
      </>
    );
  }
}
export default User;
