import React, { Component } from 'react';
import I18n from "i18n-js";
import "../../bundles/i18n/ja.js";
import { Breadcrumb,Button, Checkbox, Form, Input,Layout, Col, Row,Table,Space,Popconfirm,  } from 'antd';
import {
    EditOutlined,
    DeleteOutlined,
    SearchOutlined,
  } from "@ant-design/icons";

class listGroup  extends Component {
    state = {
        groups: [],
        searchText: "",
        searchedColumn: "",
      };

      componentDidMount() {
        console.log("DID MOUNT", I18n.t("group.id"));
        this.loadGroups();
      }

      columns = [
        {
          title: I18n.t("group.id"),
          dataIndex: "id",
          key: "id",
          sorter: (a, b) => a.id - b.id,
        },
        {
          title: I18n.t("group.name"),
          dataIndex: "name",
          key: "name",
          width: "40%",
          sorter: (a, b) => a.name.length - b.name.length,
          //...this.getColumnSearchProps("name"),
        },
        {
            title: I18n.t("group.description"),
            dataIndex: "description",
            key: "description",
            width: "40%",
            sorter: (a, b) => a.description.length - b.description.length,
            //...this.getColumnSearchProps("name"),
          },
        {
          title: I18n.t("group.action"),
          key: "action",
          render: (_text, record) => (
            <Space size="middle">
              <a onClick={() => this.editStatus(record)}>
                <EditOutlined />
              </a>
              <Popconfirm
                title="削除してもよろしいでしょうか？"
                onConfirm={() => this.deleteStatus(record)}
                okText="Yes"
                cancelText="No"
              >
                <a href="#" type="danger">
                  <DeleteOutlined />{" "}
                </a>
              </Popconfirm>
            </Space>
          ),
        },
      ];

      loadGroups = () => {
        const url = "/api/v1/group/index";
        console.log(url);
        fetch(url)
          .then((data) => {
            if (data.ok) {
              return data.json();
            }
            throw new Error("Network error.");
          })
          .then((data) => {
            data.forEach((group) => {
              const newEl = {
                key: group.id,
                id: group.id,
                name: group.group_name,
                description: group.group_description,
              };
    
              this.setState((prevState) => ({
                groups: [...prevState.groups, newEl],
              }));
            });
          })
          .catch((err) => message.error("Error: " + err));
      };
    render() { 
        return (
            <>
            <Table
              style={{marginTop:10}}
              className="table-striped-rows"
              dataSource={this.state.groups}
              columns={this.columns}
              pagination={{ pageSize: 5 }}
            />
            </>
        );
    }
}
 
export default listGroup ;
