import React from "react";
import I18n from "i18n-js";
import "../../bundles/i18n/ja.js";
import { Breadcrumb,Button, Checkbox, Form, Input,Layout, Col, Row,Table  } from 'antd';
import {
    EditOutlined,
    DeleteOutlined,
    SearchOutlined,
  } from "@ant-design/icons";

class Group extends React.Component {
    state = {
        groups: [],
        searchText: "",
        searchedColumn: "",
      };

      columns = [
        {
          title: I18n.t("group.id"),
          dataIndex: "id",
          key: "id",
          sorter: (a, b) => a.id - b.id,
        },
        {
          title: I18n.t("group.name"),
          dataIndex: "name",
          key: "name",
          width: "40%",
          sorter: (a, b) => a.name.length - b.name.length,
          //...this.getColumnSearchProps("name"),
        },
        {
          title: I18n.t("group.action"),
          key: "action",
          render: (_text, record) => (
            <Space size="middle">
              <a onClick={() => this.editStatus(record)}>
                <EditOutlined />
              </a>
              <Popconfirm
                title="削除してもよろしいでしょうか？"
                onConfirm={() => this.deleteStatus(record)}
                okText="Yes"
                cancelText="No"
              >
                <a href="#" type="danger">
                  <DeleteOutlined />{" "}
                </a>
              </Popconfirm>
            </Space>
          ),
        },
      ];

    render() {
        return (
            <>
            <Breadcrumb style={{ margin: "16px 0" }}>
              <Breadcrumb.Item>Home</Breadcrumb.Item>
              <Breadcrumb.Item>Group</Breadcrumb.Item>
            </Breadcrumb>
            
            <div
            className="site-layout-background"
            style={{ padding: 24, minHeight: 200,background: '#fff', }}
          >
            
            <Row>
            <Col span={12} offset={6}>
           
             <Form  labelCol={{
        span: 8,
      }}>
        <Form.Item
          label="グループ名:"
          name="groupName"
          rules={[
            {
              required: true,
            },
          ]}
        >
          <Input />
        </Form.Item>
  
        <Form.Item
          label="グループ概要:"
          name="groupDescription"
          rules={[
            {
              required: true,
            },
          ]}
        >
          <Input />
        </Form.Item>
  
        <Form.Item
          wrapperCol={{
            offset: 8,
            span: 16,
          }}
        >
          <Button type="primary" htmlType="submit">
            保存
          </Button>
        </Form.Item>
      </Form>
        
            </Col>
          </Row>
         
          </div>

          <Table
              className="table-striped-rows"
              dataSource={this.state.groups}
              columns={this.columns}
              pagination={{ pageSize: 5 }}
            />
          </>
           
        
       );
    }
  }

  export default Group;
