import React from "react";
import I18n from "i18n-js";
import "../../bundles/i18n/ja.js";
import Entry from "./_entry.jsx";
import List from "./_list.jsx";
import {
  Breadcrumb,
  Button,
  Checkbox,
  Form,
  Input,
  Layout,
  Col,
  Row,
  Table,
  Space,
  Popconfirm,
  message,
  Modal,
} from "antd";
import {
  EditOutlined,
  DeleteOutlined,
  SearchOutlined,
} from "@ant-design/icons";

class Group extends React.Component {
  state = {
    groups: [],
    searchText: "",
    searchedColumn: "",
    group_name: "",
    group_description: "",
    editGroups: [],
    editStatus: false,
  };

  formRef = React.createRef();

  componentDidMount() {
    this.loadGroups();
  }

  // 取得グループ
  loadGroups = () => {
    const url = "/api/v1/group/index";

    fetch(url)
      .then((data) => {
        if (data.ok) {
          return data.json();
        }
        throw new Error("Network error.");
      })
      .then((data) => {
        data.forEach((group) => {
          const newEl = {
            key: group.id,
            id: group.id,
            name: group.group_name,
            description: group.group_description,
          };

          this.setState((prevState) => ({
            groups: [...prevState.groups, newEl],
          }));
        });
      })
      .catch((err) => message.error("Error: " + err));
  };

  //グループ　更新。
  editGroup = (values, currentForm) => {
    fetch(`/api/v1/group/update/${values.id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(values),
    }).then((data) => {
      if (data.ok) {
        // const modal = Modal.success({
        //   title: I18n.t("message.M005"),
        // });
        // setTimeout(() => modal.destroy(), 1000);
        alert(I18n.t("message.M005"));
        return data.json();
      } else {
        // const modal = Modal.error({
        //   title: I18n.t("message.M058"),
        // });
        // setTimeout(() => modal.destroy(), 1000);
        alert(I18n.t("message.M058"));
      }
    });

    // this.setState({ groups: [] });
    // this.loadGroups();

    // this.setState({
    //   groups: this.state.groups.map((x) =>
    //     x.id === data.id ? { ...x, attr: "val" } : x
    //   ),
    // });

    item = this.state.groups[data.id];
    console.log(item);
    //  item.name = 'newName';
    //  items[1] = item;

    this.setState({ items: items });

    currentForm.current.resetFields();
  };

  actionNewOrEdit = (values, currentForm) => {
    if (values.id > 0) {
      this.editGroup(values, currentForm);
    } else {
      this.createGroup(values, currentForm);
    }
  };

  //ＤＢ にグループを登録すする
  createGroup = (values, currentForm) => {
    const url = "/api/v1/group/create";

    // fetch(`/api/v1/group/get/${values.id}`)
    //   .then((data) => {
    //     if (data.ok) {
    //       const modal = Modal.warning({
    //         title: I18n.t("message.M009"),
    //       });
    //       return data.json();
    //     } else {
    fetch(url, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(values),
    })
      .then((data) => {
        if (data.ok) {
          // const modal = Modal.success({
          //   title: I18n.t("message.M004"),
          // });
          // setTimeout(() => modal.destroy(), 1000);

          alert(I18n.t("message.M004"));
          return data.json();
        } else {
          // const modal = Modal.error({
          //   title: I18n.t("message.M056"),
          // });
          // setTimeout(() => modal.destroy(), 1000);
          alert(I18n.t("message.M056"));
        }
      })
      .then((data) => {
        const newEl = {
          key: data.id,
          id: data.id,
          name: data.group_name,
          description: data.group_description,
        };

        this.setState((prevState) => ({
          groups: [...prevState.groups, newEl],
        }));
      });

    currentForm.current.resetFields();
    // }
    // throw new Error("Network error.");
    // })
    // .then((data) => {
    //   console.log(data.groups);
    //   data.forEach((group) => {
    //     const newEl = {
    //       key: group.id,
    //       id: group.id,
    //       name: group.group_name,
    //       description: group.group_description,
    //     };

    //     this.setState((prevState) => ({
    //       groups: [...prevState.groups, newEl],
    //     }));
    //   });
    // })
    // .catch((err) => message.error("Error: " + err));
  };

  addNewGroup(group) {
    this.setState({
      groups: this.state.groups.concat(group),
    });
  }

  //更新の削除
  deleteStatus = (e) => {
    const url = "/api/v1/group/destroy";
    fetch(`/api/v1/group/destroy/${e.id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(e),
    }).then((data) => {
      if (data.ok) {
        // const modal = Modal.success({
        //   title: I18n.t("message.M007"),
        // });
        // setTimeout(() => modal.destroy(), 1000);
        alert(I18n.t("message.M007"));
        this.removeGroupFromList(e.id);
        return data.json();
      } else {
        // const modal = Modal.error({
        //   title: I18n.t("message.M059"),
        // });
        // setTimeout(() => modal.destroy(), 1000);
        alert(I18n.t("message.M059"));
      }
    });

    // this.setState({ groups: [] });
    // this.loadGroups();
  };

  editStatus = (e) => {
    this.formRef.current.setFieldsValue({
      group_name: e.name,
      group_description: e.description,
      id: e.id,
    });

    this.setState({ editStatus: true });
  };

  //登録画面の設定
  setForm = (currentForm) => {
    this.formRef = currentForm;
  };

  //一連で　グループを　削除
  removeGroupFromList(id) {
    const newGroup = this.state.groups.filter((group) => group.id !== id);
    this.setState({
      groups: newGroup,
    });
  }

  render() {
    return (
      <Layout style={{ minHeight: "100vh" }}>
        <Entry
          createGroup={this.actionNewOrEdit}
          formData={this.state.editGroups}
          group_name="{this.state.group_name}"
          group_description="{this.state.group_description}"
          setForm={this.setForm}
        />
        <List
          dataSource={this.state.groups}
          deleteStatus={this.deleteStatus}
          editStatus={this.editStatus}
        />
      </Layout>
    );
  }
}

export default Group;
