import React from "react";
import I18n from "i18n-js";
import "../../bundles/i18n/ja.js";
import { Form, Input, Button, Select, Alert, InputNumber } from "antd";

const NewTemplate = (props) => {
  // テンプレートデータをドロップダウンに設定する
  var templates = props.templateTypes.map((product) => {
    return (
      <Select.Option key={product.key} value={product.key}>
        {product.template_type}
      </Select.Option>
    );
  });

  const [form] = Form.useForm();

  // フォームにデータを設定する
  React.useEffect(() => {
    form.setFieldsValue({
      key: props.editPolicy.key,
      id: props.editPolicy.id,
      template_type_id: props.editPolicy.template_type_id,
      display_order: props.editPolicy.display_order,
      template_name: props.editPolicy.template_name,
      template_description: props.editPolicy.template_description,
    });
  });

  return (
    <>
      {props.visible ? (
        <Alert
          className="alert-message-style"
          message={props.status}
          type={props.type}
          closable
          afterClose={props.handleClose}
        />
      ) : null}
      <Form
        form={form}
        id="form1"
        onFinish={props.onFinish}
        {...props.layout}
        style={{ paddingTop: 8, alignItems: "center" }}
      >
        <Form.Item name="key" noStyle hidden>
          <Input />
        </Form.Item>

        <Form.Item name="id" noStyle hidden>
          <Input />
        </Form.Item>

        <Form.Item
          name="template_type_id"
          label={I18n.t("writingPolicy.templateType")}
          rules={[
            {
              required: true,
              message: I18n.t("message.M055"),
            },
          ]}
        >
          <Select style={{ width: 300 }}>{templates}</Select>
        </Form.Item>
        <Form.Item
          name="display_order"
          label={I18n.t("writingPolicy.displayOrder")}
          rules={[
            {
              required: true,
              message: I18n.t("message.M013"),
            },
            ({ getFieldValue }) => ({
              validator(_, value) {
                if (
                  getFieldValue("display_order") < 1 &&
                  getFieldValue("display_order") != null
                ) {
                  var message1 = I18n.t("message.M070");
                  return Promise.reject(message1);
                }
                return Promise.resolve();
              },
            }),
          ]}
        >
          <InputNumber style={{ width: 300 }} maxLength={9}/>
        </Form.Item>
        <Form.Item
          name="template_name"
          label={I18n.t("writingPolicy.templateName")}
          rules={[
            {
              required: true,
              message: I18n.t("message.M035"),
            },
          ]}
        >
          <Input style={{ width: 300 }} maxLength={255} />
        </Form.Item>
        <Form.Item
          name="template_description"
          label={I18n.t("writingPolicy.templateDescription")}
          rules={[
            {
              required: true,
              message: I18n.t("message.M037"),
            },
          ]}
        >
          <Input.TextArea
            style={{ width: 300, resize: "none", minHeight: 100 }}
            maxLength={255}
          />
        </Form.Item>

        <Form.Item wrapperCol={{ ...props.layout.wrapperCol, offset: 10 }}>
          <Button type="primary" htmlType="submit">
            {I18n.t("common.save")}
          </Button>
        </Form.Item>
      </Form>
    </>
  );
};

export default NewTemplate;
