import React, { Component } from 'react';
import I18n from "i18n-js";
import "../../bundles/i18n/ja.js";
import { Breadcrumb, Button, Checkbox, Form, Input, Layout, Col, Row, Table, Space, Popconfirm, } from 'antd';
import {
    EditOutlined,
    DeleteOutlined,
    SearchOutlined,
} from "@ant-design/icons";

class listGroup extends Component {
    state = {
        groups: [],
        searchText: "",
        searchedColumn: "",
    };

    componentDidMount() {
        this.loadGroups();
    }

    getColumnSearchProps = (dataIndex) => ({
        filterDropdown: ({
          setSelectedKeys,
          selectedKeys,
          confirm,
          clearFilters,
        }) => (
          <div style={{ padding: 8 }}>
            <Input
              ref={(node) => {
                this.searchInput = node;
              }}
              placeholder={`Search ${dataIndex}`}
              value={selectedKeys[0]}
              onChange={(e) =>
                setSelectedKeys(e.target.value ? [e.target.value] : [])
              }
              onPressEnter={() =>
                this.handleSearch(selectedKeys, confirm, dataIndex)
              }
              style={{ marginBottom: 8, display: "block" }}
            />
            <Space>
              <Button
                type="primary"
                onClick={() => this.handleSearch(selectedKeys, confirm, dataIndex)}
                icon={<SearchOutlined />}
                size="small"
                style={{ width: 90 }}
              >
                Search
              </Button>
              <Button
                onClick={() => this.handleReset(clearFilters)}
                size="small"
                style={{ width: 90 }}
              >
                Reset
              </Button>
              <Button
                type="link"
                size="small"
                onClick={() => {
                  confirm({ closeDropdown: false });
                  this.setState({
                    searchText: selectedKeys[0],
                    searchedColumn: dataIndex,
                  });
                }}
              >
                Filter
              </Button>
            </Space>
          </div>
        ),
        filterIcon: (filtered) => (
          <SearchOutlined style={{ color: filtered ? "#1890ff" : undefined }} />
        ),
        onFilter: (value, record) =>
          record[dataIndex]
            ? record[dataIndex]
                .toString()
                .toLowerCase()
                .includes(value.toLowerCase())
            : "",
        onFilterDropdownVisibleChange: (visible) => {
          if (visible) {
            setTimeout(() => this.searchInput.select(), 100);
          }
        },
        render: (text) =>
          this.state.searchedColumn === dataIndex ? (
            <Highlighter
              highlightStyle={{ backgroundColor: "#ffc069", padding: 0 }}
              searchWords={[this.state.searchText]}
              autoEscape
              textToHighlight={text ? text.toString() : ""}
            />
          ) : (
            text
          ),
      });
    
      handleSearch = (selectedKeys, confirm, dataIndex) => {
        confirm();
        this.setState({
          searchText: selectedKeys[0],
          searchedColumn: dataIndex,
        });
      };

    columns = [
        {
            title: "番号",
            dataIndex: "id",
            key: "id",
            //sorter: (a, b) => a.id - b.id,
        },
        {
            title: "グループ名",
            dataIndex: "name",
            key: "name",
            width: "40%",
            sorter: (a, b) => a.name.length - b.name.length,
            ...this.getColumnSearchProps("name"),
        },
        {
            title: "グループ概要",
            dataIndex: "description",
            key: "description",
            width: "40%",
            sorter: (a, b) => a.description.length - b.description.length,
            //...this.getColumnSearchProps("name"),
        },
        {
            title: "操作",
            key: "action",
            render: (_text, record) => (
                <Space size="middle">
                    <Tooltip placement="topLeft" title='edit group'>
                    <a onClick={() => this.editStatus(record)}>
                        <EditOutlined />
                    </a>

                    </Tooltip>
                    
                    <Popconfirm
                        title="削除してもよろしいでしょうか？"
                        onConfirm={() => this.deleteStatus(record)}
                        okText="Yes"
                        cancelText="No"
                    >
                        <Tooltip placement="topLeft" title='delete group'>
                        <a href="#" type="danger">
                            <DeleteOutlined />{" "}
                        </a>
                        </Tooltip>
                        
                    </Popconfirm>
                </Space>
            ),
        },
    ];

    loadGroups = () => {
        const url = "/api/v1/group/index";
        console.log(url);
        fetch(url)
            .then((data) => {
                if (data.ok) {
                    return data.json();
                }
                throw new Error("Network error.");
            })
            .then((data) => {
                data.forEach((group) => {
                    const newEl = {
                        key: group.id,
                        id: group.id,
                        name: group.group_name,
                        description: group.group_description,
                    };

                    this.setState((prevState) => ({
                        groups: [...prevState.groups, newEl],
                    }));
                });
            })
            .catch((err) => message.error("Error: " + err));
    };
    render() {
        return (
            <>
                <Table bordered
                    style={{ marginTop: 10 }}
                    className="table-striped-rows"
                    dataSource={this.state.groups}
                    columns={this.columns}
                    pagination={{ pageSize: 10 }}
                />
            </>
        );
    }
}

export default listGroup;
