import React from "react";
import I18n from "i18n-js";
import "../../bundles/i18n/ja.js";

import {
  Breadcrumb,
  Form,
  Input,
  Button,
  Table,
  message,
  Popconfirm,
  Space,
} from "antd";
import {
  EditOutlined,
  DeleteOutlined,
  SearchOutlined,
} from "@ant-design/icons";

class Template extends React.Component {
  state = {
    samples: [],
    searchText: "",
    searchedColumn: "",
  };

  componentDidMount() {
    this.loadTemplates();
  }

  getColumnSearchProps = (dataIndex) => ({
    filterDropdown: ({
      setSelectedKeys,
      selectedKeys,
      confirm,
      clearFilters,
    }) => (
      <div style={{ padding: 8 }}>
        <Input
          ref={(node) => {
            this.searchInput = node;
          }}
          placeholder={`Search ${dataIndex}`}
          value={selectedKeys[0]}
          onChange={(e) =>
            setSelectedKeys(e.target.value ? [e.target.value] : [])
          }
          onPressEnter={() =>
            this.handleSearch(selectedKeys, confirm, dataIndex)
          }
          style={{ marginBottom: 8, display: "block" }}
        />
        <Space>
          <Button
            type="primary"
            onClick={() => this.handleSearch(selectedKeys, confirm, dataIndex)}
            icon={<SearchOutlined />}
            size="small"
            style={{ width: 90 }}
          >
            Search
          </Button>
          <Button
            onClick={() => this.handleReset(clearFilters)}
            size="small"
            style={{ width: 90 }}
          >
            Reset
          </Button>
          <Button
            type="link"
            size="small"
            onClick={() => {
              confirm({ closeDropdown: false });
              this.setState({
                searchText: selectedKeys[0],
                searchedColumn: dataIndex,
              });
            }}
          >
            Filter
          </Button>
        </Space>
      </div>
    ),
    filterIcon: (filtered) => (
      <SearchOutlined style={{ color: filtered ? "#1890ff" : undefined }} />
    ),
    onFilter: (value, record) =>
      record[dataIndex]
        ? record[dataIndex]
            .toString()
            .toLowerCase()
            .includes(value.toLowerCase())
        : "",
    onFilterDropdownVisibleChange: (visible) => {
      if (visible) {
        setTimeout(() => this.searchInput.select(), 100);
      }
    },
    render: (text) =>
      this.state.searchedColumn === dataIndex ? (
        <Highlighter
          highlightStyle={{ backgroundColor: "#ffc069", padding: 0 }}
          searchWords={[this.state.searchText]}
          autoEscape
          textToHighlight={text ? text.toString() : ""}
        />
      ) : (
        text
      ),
  });

  handleSearch = (selectedKeys, confirm, dataIndex) => {
    confirm();
    this.setState({
      searchText: selectedKeys[0],
      searchedColumn: dataIndex,
    });
  };

  columns = [
    {
      title: I18n.t("writingPolicy.id"),
      dataIndex: "id",
      key: "id",
      align: "center"
    },
    {
      title: I18n.t("writingPolicy.templateType"),
      dataIndex: "template_type",
      key: "template_type",
      width: "35%",
      align: "center",
      sorter: (a, b) => a.template_type.length - b.template_type.length,
      ...this.getColumnSearchProps("template_type"),
    },
    {
        title: I18n.t("writingPolicy.displayOrder"),
        dataIndex: "display_order",
        key: "display_order",
      },
      {
        title: I18n.t("writingPolicy.templateName"),
        dataIndex: "template_name",
        key: "template_name",
        width: "40%",
        sorter: (a, b) => a.templateName.length - b.templateName.length,
        ...this.getColumnSearchProps("template_name"),
      },
    {
      title: I18n.t("writingPolicy.action"),
      key: "action",
      render: (_text, record) => (
        <Space size="middle">
          <a onClick={() => this.editStatus(record)}>
            <EditOutlined />
          </a>
          <Popconfirm
            title="削除してもよろしいでしょうか？"
            onConfirm={() => this.deleteStatus(record)}
            okText="Yes"
            cancelText="No"
          >
            <a href="#" type="danger">
              <DeleteOutlined />{" "}
            </a>
          </Popconfirm>
        </Space>
      ),
    },
  ];

  loadTemplates = () => {
    const url = "/api/v1/templates/index";
    console.log(url);
    fetch(url)
      .then((data) => {
        if (data.ok) {
          return data.json();
        }
        throw new Error("Network error.");
      })
      .then((data) => {
        data.forEach((templates) => {
          const newEl = {
            key: templates.id,
            id: templates.id,
            template_type: templates.template_type,
            display_order: templates.display_order,
               
          };

          this.setState((prevState) => ({
            samples: [...prevState.samples, newEl],
          }));
        });
      })
      .catch((err) => message.error("Error: " + err));
  };

  render() {
    return (
      <>
      
        <Breadcrumb style={{ margin: "16px 0" }}>
          <Breadcrumb.Item>Home</Breadcrumb.Item>
          <Breadcrumb.Item>Sample</Breadcrumb.Item>
        </Breadcrumb>
        <div
          className="site-layout-background"
          style={{ padding: 24, minHeight: 360 }}
        >
          <Table
            className="table-striped-rows"
            dataSource={this.state.samples}
            columns={this.columns}
            pagination={{ pageSize: 5 }}
          />
        </div>
      </>
    );
  }
}

export default Template;
