import React from 'react';
import I18n from "i18n-js";
import "../../bundles/i18n/ja.js";

import {
    Card,
    Form,
    Input,
    Button,
    Select,
    Table,
    message,
    Popconfirm,
    Space,
} from "antd";
import {
    EditOutlined,
    DeleteOutlined,
    SearchOutlined,
} from "@ant-design/icons";

const NewTemplate = (props) => {

    const state = {
        templateTypes: [],
        list: [],
    };

    let formFields = {}
   
    const layout = {
        labelCol: {
            span: 8,
        },
        wrapperCol: {
            span: 16,
        },
        textalign: "center",
    };

    

    componentDidMount = () => {
        this.loadTypes();
      }

    return(
        <Card  
        style={
            { 
                width: 1070,
                marginLeft: 25, 
                marginRight: 25, 
                marginTop: 25, 
                paddingBottom: 20, 
                paddingLeft: 50, 
                height: 380 
            }
        } >
        <Form onSubmit={ (e) => { 
            e.preventDefault();
            props.handleFormSubmit(
                formFields.name.value,
                formFields.description.value
            ); 
            e.target.reset();}
            } {...layout}  
            >
        <Form.Item
            name="template_type"
            label="テンプレート種類"
            rules={[
                {
                    required: true,
                    message: I18n.t("message.M055"),
                },
            ]}
        >
            <Select　style={{width: 300}}>
                {/* <Select.Option value="choose"></Select.Option>
                <Select.Option value="1">記事の幅</Select.Option>
                <Select.Option value="2">記事の深さ</Select.Option>
                <Select.Option value="3">記事の読みやすさ</Select.Option>
                <Select.Option value="4">見出し詳細</Select.Option> */}
                {this.props.getTypes}
            </Select>
        </Form.Item>
        <Form.Item
        name="display_order"
        label="表示順"
        rules={[
          {
            required: true,
            message: I18n.t("message.M013"),
          },
        ]}
        >
            <Input style={{width: 300}} />
        </Form.Item>

        <Form.Item
        name="template_name"
        label="テンプレート名称"
        rules={[
          {
            required: true,
            message: I18n.t("message.M035"),
          },
        ]}
        >
            <Input style={{width: 300}} />
        </Form.Item>
      
        <Form.Item 
        name="description"
        label="テンプレート内容" 
        rules={[
          {
            required: true,
            message: I18n.t("message.M037"),
          },
        ]}>
            <Input.TextArea style={{width: 300, resize: "none", minHeight: 100 }} />
        </Form.Item>
        <Form.Item wrapperCol={{ ...layout.wrapperCol, offset: 8 }}>
            <Button type="primary" htmlType="submit">
                保存
            </Button>
        </Form.Item>
    </Form>
    </Card>
    )
  }
export default NewTemplate;
