import React from "react";
import I18n from "i18n-js";
import "../../bundles/i18n/ja.js";
import Entry from "./_entry.jsx";
import List from "./_list.jsx";
import axios from "axios";
import {
  Breadcrumb,
  Button,
  Checkbox,
  Form,
  Input,
  Layout,
  Col,
  Row,
  Table,
  Space,
  Popconfirm,
} from "antd";
import {
  EditOutlined,
  DeleteOutlined,
  SearchOutlined,
} from "@ant-design/icons";

class Group extends React.Component {
  state = {
    groups: [],
    searchText: "",
    searchedColumn: "",
  };

  componentDidMount() {
    this.loadGroups();
  }

  loadGroups = () => {
    const url = "/api/v1/group/index";
    console.log(url);
    fetch(url)
      .then((data) => {
        if (data.ok) {
          return data.json();
        }
        throw new Error("Network error.");
      })
      .then((data) => {
        data.forEach((group) => {
          const newEl = {
            key: group.id,
            id: group.id,
            name: group.group_name,
            description: group.group_description,
          };

          this.setState((prevState) => ({
            groups: [...prevState.groups, newEl],
          }));
        });
      })
      .catch((err) => message.error("Error: " + err));
  };

  createGroup = (values) => {
    console.log("Success1:", values);
    const url = "/api/v1/group/create";

    fetch(url, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(values),
    })
      .then((data) => {
        if (data.ok) {
          return data.json();
        }
      })
      .then((data) => {
        console.log(data);
        data.forEach((key) => {
          const newEl = {
            key: group.id,
            id: group.id,
            name: group.group_name,
            description: group.group_description,
          };

          this.setState({
            groups: newEl,
          });
        });
      });
  };

  // .then((group) => {
  //   this.addNewGroup(group);
  // });

  addNewGroup(group) {
    this.setState({
      groups: this.state.groups.concat(group),
    });
  }

  render() {
    return (
      <Layout style={{ minHeight: "100vh" }}>
        <Entry createGroup={this.createGroup} />
        <List dataSource={this.state.groups} />
      </Layout>
    );
  }
}

export default Group;
