import React from "react";
import NewTemplate from "./_entry";
import TemplateList from "./_list";

import {
  Breadcrumb,
  Form,
  Input,
  Button,
  Table,
  message,
  Popconfirm,
  Space,
} from "antd";
import {
  EditOutlined,
  DeleteOutlined,
  SearchOutlined,
} from "@ant-design/icons";

class Main extends React.Component {

  state = {
        templateTypes: [],
        list: [],
    };

  loadTypes = () => {
        const url = "/api/v1/templates/index";
        console.log(url);
        fetch(url)
            .then((data) => {
            if (data.ok) {
                return data.json();
            }
            throw new Error("Network error.");
        })
        .then((data) => {
            data.forEach((templates) => {
                const newEl = {
                    key: templates.id,
                    id: templates.id,
                    template_type: templates.template_type,
                };

                this.setState((prevState) => ({
                    templateTypes: [...prevState.templateTypes, newEl],
                }));
            });
        })
        console.log(templateTypes.template_type)
      .catch((err) => message.error("Error: " + err));
    };

    getTypes = () => {
      this.loadTypes();
      this.state.templateTypes.forEach((product) => {
        list.push(<Select.Option value={product.id}>{product.template_type}</Select.Option>);
    });
    }

  render() {
    return(
      <>
      <NewTemplate list={this.state.list}/>
      <TemplateList/>
      </>
    );
  }
}

export default Main;
