import React from "react";
import NewTemplate from "./_entry";
import TemplateList from "./_list";
import I18n from "i18n-js";
import "../../bundles/i18n/ja.js";
import Highlighter from "react-highlight-words";

import { Input, Button, Popconfirm, Space, Card } from "antd";
import {
  EditOutlined,
  DeleteOutlined,
  SearchOutlined,
} from "@ant-design/icons";

class Main extends React.Component {
  state = {
    templateTypes: [],
    policies: [],
    dump: [],
    list: [],
    searchText: "",
    searchedColumn: "",
    status: "",
    visible: false,
    type: "",
    editable: false,
    editPolicy: [],
  };

  layout = {
    labelCol: {
      span: 10,
    },
    wrapperCol: {
      span: 10,
    },
    textalign: "center",
  };

  // テーブル列検索処理
  getColumnSearchProps = (dataIndex) => ({
    filterDropdown: ({
      setSelectedKeys,
      selectedKeys,
      confirm,
      clearFilters,
    }) => (
      <div style={{ padding: 8 }}>
        <Input
          ref={(node) => {
            this.searchInput = node;
          }}
          value={selectedKeys[0]}
          onChange={(e) =>
            setSelectedKeys(e.target.value ? [e.target.value] : [])
          }
          onPressEnter={() =>
            this.handleSearch(selectedKeys, confirm, dataIndex)
          }
          style={{ marginBottom: 8, display: "block" }}
        />
        <Space>
          <Button
            type="primary"
            onClick={() => this.handleSearch(selectedKeys, confirm, dataIndex)}
            icon={<SearchOutlined />}
            size="small"
            style={{ width: 90 }}
          >
            {I18n.t("common.search")}
          </Button>
          <Button
            onClick={() => this.handleReset(clearFilters)}
            size="small"
            style={{ width: 90 }}
          >
            {I18n.t("common.reset")}
          </Button>
          <Button
            type="link"
            size="small"
            onClick={() => {
              confirm({ closeDropdown: false });
              this.setState({
                searchText: selectedKeys[0],
                searchedColumn: dataIndex,
              });
            }}
          >
            {I18n.t("common.filter")}
          </Button>
        </Space>
      </div>
    ),
    filterIcon: (filtered) => (
      <SearchOutlined style={{ color: filtered ? "#1890ff" : undefined }} />
    ),
    onFilter: (value, record) =>
      record[dataIndex]
        ? record[dataIndex]
            .toString()
            .toLowerCase()
            .includes(value.toLowerCase())
        : "",
    onFilterDropdownVisibleChange: (visible) => {
      if (visible) {
        setTimeout(() => this.searchInput.select(), 100);
      }
    },
    render: (text) =>
      this.state.searchedColumn === dataIndex ? (
        <Highlighter
          highlightStyle={{ backgroundColor: "#ffc069", padding: 0 }}
          searchWords={[this.state.searchText]}
          autoEscape
          textToHighlight={text ? text.toString() : ""}
        />
      ) : (
        text
      ),
  });

  // 執筆方針テンプレート削除処理
  deletePolicy = (key) => {
    const url = "/api/v1/writing_policy/destroy/" + key;
    fetch(url, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((data) => {
        if (data.ok) {
          return data.json();
        }
        throw new Error();
      })
      .then((data) => {
        if (data.status) {
          // 成功メッセージを表示する
          // M007: 削除しました。
          this.setState({
            status: I18n.t("message.M007"),
            visible: true,
            type: "success",
            policies: [],
          });
          // データを順序する
          this.getPolicyData(data.writingPolicy);
        } else {
          // エラーメッセージを表示する
          // M059: データ削除に失敗しました。
          this.showMessage("M059");
        }
        this.doReset();
      })
      .catch(() => {
        // エラーメッセージを表示する
        // M059: データ削除に失敗しました。
        this.showMessage("M059");
      });
  };

  //アラートメッセージ止める処理
  handleClose = () => {
    this.setState({ visible: false });
  };

  // 更新のためテーブルからデータをformに引っ張り処理
  handleEdit = (record) => {
    this.setState({
      editable: true,
      editPolicy: {
        key: record.key,
        id: record.id,
        display_order: record.display_order,
        template_name: record.template_name,
        template_type_id: record.template_type_id,
        template_description: record.template_description,
      },
    });
  };

  columns = [
    {
      title: I18n.t("writingPolicy.id"),
      dataIndex: "id",
      key: "id",
      align: "center",
    },
    {
      title: I18n.t("writingPolicy.templateType"),
      dataIndex: "template_type",
      key: "template_type",
      width: "35%",
      sorter: (a, b) => a.template_type.localeCompare(b.template_type),
      ...this.getColumnSearchProps("template_type"),
    },
    {
      title: I18n.t("writingPolicy.displayOrder"),
      dataIndex: "display_order",
      key: "display_order",
      align: "center",
    },
    {
      title: I18n.t("writingPolicy.templateName"),
      dataIndex: "template_name",
      key: "template_name",
      width: "35%",
      sorter: (a, b) => a.template_name.localeCompare(b.template_name),
      ...this.getColumnSearchProps("template_name"),
    },
    {
      title: I18n.t("common.action"),
      key: "action",
      align: "center",
      render: (_text, record) => (
        <Space size="middle">
          <a onClick={() => this.handleEdit(record)}>
            <EditOutlined />
          </a>
          <Popconfirm
            title={I18n.t("message.M006")}
            onConfirm={() => this.deletePolicy(record.key)}
            okText={I18n.t("common.okText")}
            cancelText={I18n.t("common.cancelText")}
          >
            <a href="#" type="danger">
              <DeleteOutlined />{" "}
            </a>
          </Popconfirm>
        </Space>
      ),
    },
  ];

  // テーブル列検索のリセット処理
  handleReset = (clearFilters) => {
    clearFilters();
    this.setState({ searchText: "" });
  };

  // 初期表示処理
  loadPolicy = () => {
    const url = "/api/v1/writing_policy/index";
    fetch(url)
      .then((data) => {
        if (data.ok) {
          return data.json();
        }
        throw new Error();
      })
      .then((data) => {
        data.writingPolicy.forEach((policy, index) => {
          const newEl = {
            key: policy.id,
            id: index + 1,
            template_type: policy.template_type,
            display_order: policy.display_order,
            template_name: policy.template_name,
            template_type_id: policy.template_type_id,
            template_description: policy.template_description,
          };
          this.setState((prevState) => ({
            policies: [...prevState.policies, newEl],
          }));
        });
        data.templateTypes.forEach((template) => {
          const templateName = {
            key: template.id,
            id: template.id,
            template_type: template.template_type,
          };
          this.setState((prevState) => ({
            templateTypes: [...prevState.templateTypes, templateName],
          }));
        });
      })
      .catch(() => {
        // エラーメッセージを表示する
        // M060: データ取得に失敗しました。
        this.showMessage("M060");
      });
  };

  // 執筆方針テンプレート登録処理
  onSave = (values) => {
    const url = "api/v1/writing_policy/create";
    fetch(url, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(values),
    })
      .then((data) => {
        if (data.ok) {
          return data.json();
        }
        throw new Error();
      })
      .then((data) => {
        if (data.error != "") {
          // エラーメッセージを表示する
          // M014: 表示順が既にあります。
          // M036: 既に登録されているテンプレート名称です。
          this.showMessage(data.error);
          // フォームにデータを設定する
          this.handleEdit(values);
        } else {
          if (data.status) {
            // 成功メッセージを表示する
            // M004: 登録しました。
            this.setState({
              duplicate: false,
              errorMessage: "",
              status: I18n.t("message.M004"),
              visible: true,
              type: "success",
              editPolicy: [],
              policies: [],
            });
            // データを順序する
            this.getPolicyData(data.writingPolicy);
          } else {
            // エラーメッセージを表示する
            // M056: データ登録に失敗しました。
            this.setState({
              duplicate: false,
              errorMessage: "",
              status: I18n.t("message.M056"),
              visible: true,
              type: "error",
              editPolicy: [],
            });
          }
        }
      })
      .catch(() => {
        // エラーメッセージを表示する
        // M056: データ登録に失敗しました。
        this.showMessage("M056");
      });
  };

  // 執筆方針テンプレート更新処理
  onUpdate = (values) => {
    const url = "api/v1/writing_policy/update/" + values.key;
    fetch(url, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(values),
    })
      .then((data) => {
        if (data.ok) {
          return data.json();
        }
        throw new Error();
      })
      .then((data) => {
        if (data.error != "") {
          // エラーメッセージを表示する
          // M014: 表示順が既にあります。
          // M036: 既に登録されているテンプレート名称です。
          this.showMessage(data.error);
          // フォームにデータを設定する
          this.handleEdit(values);
        } else {
          if (data.status) {
            // 成功メッセージを表示する
            // M005: 更新しました。
            this.setState({
              duplicate: false,
              errorMessage: "",
              status: I18n.t("message.M005"),
              visible: true,
              type: "success",
              editPolicy: [],
              policies: [],
            });
            // データを順序する
            this.getPolicyData(data.writingPolicy);
          } else {
            // エラーメッセージを表示する
            // M058: データ更新に失敗しました。
            this.setState({
              duplicate: false,
              errorMessage: "",
              status: I18n.t("message.M058"),
              visible: true,
              type: "error",
              editPolicy: [],
            });
          }
        }
      })
      .catch(() => {
        // エラーメッセージを表示する
        // M058: データ更新に失敗しました。
        this.showMessage("M058");
      });
  };

  // 一覧のデータを変更
  getPolicyData = (data) => {
    data.forEach((policy, index) => {
      const newEl = {
        key: policy.id,
        id: index + 1,
        template_type: policy.template_type,
        display_order: policy.display_order,
        template_name: policy.template_name,
        template_type_id: policy.template_type_id,
        template_description: policy.template_description,
      };
      this.setState((prevState) => ({
        policies: [...prevState.policies, newEl],
      }));
    });
  };

  // 更新か挿入かを決める処理
  onFinish = (values) => {
    if (values.key > 0) {
      this.onUpdate(values);
    } else {
      this.onSave(values);
    }
  };

  // テーブル列検索にある検索ボタン処理
  handleSearch = (selectedKeys, confirm, dataIndex) => {
    confirm();
    this.setState({
      searchText: selectedKeys[0],
      searchedColumn: dataIndex,
    });
  };

  // フォームに入るデータをリセットする処理
  doReset = () => {
    document.getElementById("form1").reset();
  };

  // メッセージを表示する
  showMessage = (status) => {
    this.setState({
      status: I18n.t("message." + status),
      visible: true,
      type: "error",
      editPolicy: [],
    });
  };

  // 初期表示処理
  componentDidMount() {
    this.loadPolicy();
  }

  render() {
    return (
      <>
        <div
          className="site-layout-background"
          style={{ padding: 24, minHeight: 360, align: "right" }}
        >
          <Space direction="vertical" style={{ width: "100%" }}>
            <Card>
              <NewTemplate
                onFinish={this.onFinish}
                layout={this.layout}
                templateTypes={this.state.templateTypes}
                visible={this.state.visible}
                status={this.state.status}
                type={this.state.type}
                handleClose={this.handleClose}
                editPolicy={this.state.editPolicy}
                editable={this.state.editable}
              />
            </Card>
            <TemplateList
              policies={this.state.policies}
              columns={this.columns}
              handleChange={this.handleChange}
            />
          </Space>
        </div>
      </>
    );
  }
}

export default Main;
