import React from "react";
import I18n from "i18n-js";
import "../../bundles/i18n/ja.js";

import { Form, Input, Button, Select, Alert } from "antd";

const tailLayout = {
  wrapperCol: { offset: 10, span: 16 },
};

const AddUser = (props) => {
  const [form] = Form.useForm();
  // ユーザーデータをドロップダウンに設定する
  var userLevells = props.userLevels.map((product) => {
    return (
      <Select.Option key={product.key} value={product.key}>
        {product.user_level}
      </Select.Option>
    );
  });

  var key = props.editUser.key;
  var id = props.editUser.id;
  var email = props.editUser.email;
  var user_level = props.editUser.user_level;
  var user_level_id = props.editUser.user_level_id;

  // フォームにデータを設定する
  React.useEffect(() => {
    form.setFieldsValue({
      key: key,
      id: id,
      email: email,
      user_level: user_level,
      user_level_id: user_level_id,
    });
  });

  return (
    <>
      {props.visible ? (
        <Alert
          className="alert-message-style"
          message={props.status}
          type={props.type}
          closable
          afterClose={props.handleClose}
        />
      ) : null}

      <Form
        id="form"
        form={form}
        name="control-hooks"
        style={{ paddingTop: 8 }}
        layout="horizontal"
        {...props.formlayout}
        onFinish={props.onFinish}
      >
        <Form.Item name="key" noStyle hidden>
          <Input />
        </Form.Item>

        <Form.Item name="id" noStyle hidden>
          <Input />
        </Form.Item>
        <Form.Item
          name="email"
          label={I18n.t("user.email")}
          rules={[
            {
              pattern: new RegExp(
                /^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@(?:[a-zA-Z0-9]+\.)+[a-zA-Z0-9-]{2,3}$/
              ),
              // エラーメッセージを表示する
              // M032: メールアドレスのフォーマットが正しくありません。
              message: I18n.t("message.M032"),
            },
            {
              // エラーメッセージを表示する
              // M001: ユーザー名を入力してください。
              required: true,
              message: I18n.t("message.M001"),
            },
          ]}
        >
          <Input style={{ width: 300 }} />
        </Form.Item>
        <Form.Item
          name="user_level_id"
          label={I18n.t("user.user_level")}
          rules={[
            {
              // エラーメッセージを表示する
              // M003: ユーザー権限を選択してください。
              required: true,
              message: I18n.t("message.M003"),
            },
          ]}
        >
          <Select
            onChange={props.onChange}
            style={{ textAlign: "left" }}
            style={{ width: 300 }}
          >
            {userLevells}
          </Select>
        </Form.Item>

        <Form.Item {...tailLayout}>
          <Button type="primary" htmlType="submit">
            {I18n.t("common.save")}
          </Button>
        </Form.Item>
      </Form>
    </>
  );
};

export default AddUser;
