import React from 'react';
import I18n from "i18n-js";
import "../../bundles/i18n/ja.js";

import {
    Card,
    Form,
    Input,
    Button,
    Select,
    Table,
    message,
    Popconfirm,
    Space,
} from "antd";
import {
    EditOutlined,
    DeleteOutlined,
    SearchOutlined,
} from "@ant-design/icons";

class NewTemplate extends React.Component{

    state = {
        templateTypes: [],
        list: [],
    };

    formFields = {}
   
    layout = {
        labelCol: {
            span: 8,
        },
        wrapperCol: {
            span: 16,
        },
        textalign: "center",
    };

    loadTypes = () => {
        const url = "/api/v1/template_types/index";
        console.log(url);
        fetch(url)
            .then((data) => {
            if (data.ok) {
                return data.json();
            }
            throw new Error("Network error.");
        })
        .then((data) => {
            data.forEach((templates) => { 
                const newEl = { 
                    key: templates.id,
                    id: templates.id,
                    template_type: templates.template_type,
                };

                this.setState((prevState) => ({
                    templateTypes: [...prevState.templateTypes, newEl],
                }));
            });
        })
        //console.log(this.state.templateTypes.template_type)
        .catch((err) => message.error("Error: " + err));
    };

    componentDidMount(){
        this.loadTypes();
    }

    handleChange = (e) => {
        
        var value = this.state.templateTypes.filter(function(item) {
            console.log(e);
            return item.key == e
        })
        //console.log(value[0].value);
    }

    render() {
        return(
        <Card  
        style={
            { 
                width: 1070,
                marginLeft: 25, 
                marginRight: 25, 
                marginTop: 25, 
                paddingBottom: 20, 
                paddingLeft: 50, 
                height: 380 
            }
        } >
        <Form onSubmit={ (e) => { 
            e.preventDefault();
            console.log('template type ',this.formFields.template_type.value);
            console.log('display order ',formFields.display_order.value);
            props.handleFormSubmit(
                this.formFields.template_type.value,
                this.formFields.display_order.value,
                this.formFields.template_name.value,
                this.formFields.description.value
            ); 
            e.target.reset();}
            } {...this.layout}  
            >
        <Form.Item
            name="template_type"
            label="テンプレート種類"
            rules={[
                {
                    required: true,
                    message: I18n.t("message.M055"),
                },
            ]}
        >
            <Select　style={{width: 300}} onChange={this.handleChange}>
                {
                    this.state.templateTypes.map((product,key) => <Select.Option ref={input => this.formFields.template_type = product.key} key={key} value={product.key} >{product.template_type}</Select.Option>)
                }
                
            </Select>
        </Form.Item>
        <Form.Item
        name="display_order"
        label="表示順"
        rules={[
          {
            required: true,
            message: I18n.t("message.M013"),
          },
        ]}
        >
            <Input ref={input => formFields.display_order = input} style={{width: 300}} />
        </Form.Item>

        <Form.Item
        name="template_name"
        label="テンプレート名称"
        rules={[
          {
            required: true,
            message: I18n.t("message.M035"),
          },
        ]}
        >
            <Input ref={input => this.formFields.template_name = input} style={{width: 300}} />
        </Form.Item>
      
        <Form.Item 
        name="description"
        label="テンプレート内容" 
        rules={[
          {
            required: true,
            message: I18n.t("message.M037"),
          },
        ]}>
            <Input.TextArea ref={input => this.formFields.description = input} style={{width: 300, resize: "none", minHeight: 100 }} />
        </Form.Item>
{/* 
        <Form.Item name="created_user">
            <Input type={'hidden'} value={1}/>
        </Form.Item>

        <Form.Item name="updated_user">
            <Input type={'hidden'} value={1}/>
        </Form.Item> */}

        <Form.Item wrapperCol={{ ...this.layout.wrapperCol, offset: 8 }}>
            <Button type="primary" htmlType="submit">
                保存
            </Button>
        </Form.Item>
    </Form>
    </Card>
    )
    }
    
  }
export default NewTemplate;
