import React from "react";
import { Table, Modal, Form, Input, AutoComplete } from "antd";
import I18n from "i18n-js";
import "../../bundles/i18n/ja.js";

const PopUpModal = (props) => {
  const [form] = Form.useForm();

  const row = {
    type: "radio",
    selectedRowKeys: props.selectedRowKeys,
    onChange: props.onSelectChange,
  };

  // メールアドレスをフロント担当者テキストボックスに設定する
  React.useEffect(() => {
    if (props.email != "") {
      form.setFieldsValue({
        email: props.email,
      });
    }
  });

  return (
    <>
      <Modal
        title={I18n.t("modal.title")}
        visible={props.isModalVisible}
        footer={null}
        onCancel={props.handleCancel}
      >
        <Form wrapperCol={{ span: 10 }} style={{ paddingTop: 0 }} form={form}>
          <Form.Item
            name="email"
            label={I18n.t("projectInfo.username")}
            style={{ textAlign: "left" }}
          >
            <Input
              placeholder={I18n.t("modal.search")}
              onChange={(email) => props.searchResult(email.target.value)}
            />
          </Form.Item>
        </Form>
        <Table
          rowSelection={row}
          columns={props.modalColumns}
          dataSource={props.modalList}
          pagination={{
            pageSize: 5,
            total: props.modalList.length,
            showTotal: (total, range) =>
              I18n.t("common.pagination", {
                0: total,
                1: range[0],
                2: range[1],
              }),
          }}
        />
      </Modal>
    </>
  );
};

export default PopUpModal;
