import React from "react";
import Entry from "./_entry";
import List from "./_list";
import "../../bundles/i18n/ja.js";
import I18n from "i18n-js";
import Highlighter from "react-highlight-words";
import { Input, Button, Table, message, Popconfirm, Space, Empty } from "antd";
import {
  EditOutlined,
  DeleteOutlined,
  SearchOutlined,
} from "@ant-design/icons";

class HeaderClassification extends React.Component {
  state = {
    header_classification_dicts: [],
    searchText: "",
    searchedColumn: "",
    visible: false,
    status: "",
    type: "",
    value: "",
    editable: false,
    key: "",
    display_order: "",
    header_classification: "",
    keyword: "",
    id: "",
  };
  // form1 = Form.useForm();
  doReset = () => {
    document.getElementById("form").reset();
  };

  handleCancel = () => {
    this.setState({
      visible: false,
    });
  };

  reloadHeaders = () => {
    this.setState({ header_classification_dicts: [] });
    this.loadHeaders();
    this.doReset();
  };
  componentDidMount() {
    this.loadHeaders();
  }
  getColumnSearchProps = (dataIndex) => ({
    filterDropdown: ({
      setSelectedKeys,
      selectedKeys,
      confirm,
      clearFilters,
    }) => (
      <div style={{ padding: 8 }}>
        <Input
          ref={(node) => {
            this.searchInput = node;
          }}
          placeholder={`Search ${dataIndex}`}
          value={selectedKeys[0]}
          onChange={(e) =>
            setSelectedKeys(e.target.value ? [e.target.value] : [])
          }
          onPressEnter={() =>
            this.handleSearch(selectedKeys, confirm, dataIndex)
          }
          style={{ marginBottom: 8, display: "block" }}
        />
        <Space>
          <Button
            type="primary"
            onClick={() => this.handleSearch(selectedKeys, confirm, dataIndex)}
            icon={<SearchOutlined />}
            size="small"
            style={{ width: 90 }}
          >
            {I18n.t("common.search")}
          </Button>
          <Button
            onClick={() => this.handleReset(clearFilters)}
            size="small"
            style={{ width: 90 }}
          >
            {I18n.t("common.reset")}
          </Button>
          <Button
            type="link"
            size="small"
            onClick={() => {
              confirm({ closeDropdown: false });
              this.setState({
                searchText: selectedKeys[0],
                searchedColumn: dataIndex,
              });
            }}
          >
            {I18n.t("common.filter")}
          </Button>
        </Space>
      </div>
    ),
    filterIcon: (filtered) => (
      <SearchOutlined style={{ color: filtered ? "#1890ff" : undefined }} />
    ),
    onFilter: (value, record) =>
      record[dataIndex]
        ? record[dataIndex]
            .toString()
            .toLowerCase()
            .includes(value.toLowerCase())
        : "",
    onFilterDropdownVisibleChange: (visible) => {
      if (visible) {
        setTimeout(() => this.searchInput.select(), 100);
      }
    },
    render: (text) =>
      this.state.searchedColumn === dataIndex ? (
        <Highlighter
          highlightStyle={{ backgroundColor: "#ffc069", padding: 0 }}
          searchWords={[this.state.searchText]}
          autoEscape
          textToHighlight={text ? text.toString() : ""}
        />
      ) : (
        text
      ),
  });

  handleSearch = (selectedKeys, confirm, dataIndex) => {
    confirm();
    this.setState({
      searchText: selectedKeys[0],
      searchedColumn: dataIndex,
    });
  };
  columns = [
    {
      title: I18n.t("headerClassificationDict.id"),
      dataIndex: "id",
      key: "id",
      align: "center",
    },
    {
      title: I18n.t("headerClassificationDict.displayOrder"),
      dataIndex: "display_order",
      key: "display_order",
      align: "center",
      ...this.getColumnSearchProps("display_order"),
    },
    {
      title: I18n.t("headerClassificationDict.headerClassification"),
      dataIndex: "header_classification",
      key: "header_classification",
      align: "center",

      ...this.getColumnSearchProps("header_classification"),
    },
    {
      title: I18n.t("headerClassificationDict.keyword"),
      dataIndex: "keyword",
      key: "keyword",
      width: "35%",
      align: "center",

      ...this.getColumnSearchProps("keyword"),
    },
    {
      title: I18n.t("headerClassificationDict.action"),
      key: "action",
      align: "center",
      render: (_text, record) => (
        <Space size="middle">
          <a onClick={() => this.handleEdit(record)}>
            <EditOutlined />
          </a>
          <Popconfirm
            title={I18n.t("message.M006")}
            onConfirm={() => this.deleteStatus(record.id)}
            okText={I18n.t("common.okText")}
            cancelText={I18n.t("common.cancelText")}
          >
            <a href="#" type="danger">
              <DeleteOutlined />{" "}
            </a>
          </Popconfirm>
        </Space>
      ),
    },
  ];
  handleReset = (clearFilters) => {
    clearFilters();
    this.setState({ searchText: "" });
  };
  handleClose = () => {
    this.setState({ visible: false });
  };

  deleteStatus = (id) => {
    const url = "api/v1/header_classification_dicts/" + id;

    fetch(url, {
      method: "delete",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((data) => {
        if (data.ok) {
          this.setState({ status: I18n.t("message.M007") });
          this.setState({ visible: true });
          this.setState({ type: "success" });
          return data.json();
        } else {
          this.setState({ status: I18n.t("message.M059") });
          this.setState({ visible: true });
          this.setState({ type: "error" });
        }
      })
      .then(() => {
        this.reloadHeaders();
      })
      .catch((err) => message.error("Error: " + err));
  };

  handleEdit = (record) => {
    this.setState({ editable: true });
    this.state.editHeader = {
      key: record.key,
      id: record.key,
      display_order: record.display_order,
      header_classification: record.header_classification,
      keyword: record.keyword,
    };
    this.setState(this.state.editHeader);
    this.setState({ key: record.key });
    this.setState({ id: record.key });
    this.setState({ display_order: record.display_order });
    this.setState({
      header_classification: record.header_classification,
    });
    this.setState({ keyword: record.keyword });
    // form1.setFieldsValue({
    //   key: record.key,
    //   id: record.id,
    //   display_order: record.display_order,
    //   header_classification: record.header_classification,
    //   keyword: record.keyword,
    // });
    console.log("this.state.editHeader", this.state.editHeader);
  };

  onSave = (values) => {
    const url = "api/v1/header_classification_dicts/create";
    fetch(url, {
      method: "post",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(values),
    })
      .then((data) => {
        if (data.status) {
          console.log("data ok");
          this.setState({ status: I18n.t("message.M004") });
          this.setState({ visible: true });
          this.setState({ type: "success" });
          this.reloadHeaders();
        } else {
          console.log("data not ok");
          this.setState({ status: I18n.t("message.M056") });
          this.setState({ visible: true });
          this.setState({ type: "error" });
          this.reloadHeaders();
        }
      })
      .catch((err) => console.error("Error: " + err));
  };

  onUpdate = (values) => {
    const url = "api/v1/header_classification_dicts/update/" + values.id;
    fetch(url, {
      method: "post",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(values),
    })
      .then((data) => {
        return data.json();
      })
      .then((data) => {
        if (data.status) {
          console.log("data ok");
          this.setState({ status: I18n.t("message.M005") });
          this.setState({ visible: true });
          this.setState({ type: "success" });
          this.setState({ display_order: Empty });
          this.setState({ header_classification: Empty });
          this.setState({ keyword: Empty });
          this.reloadHeaders();
        } else {
          console.log("data not ok");
          this.setState({ status: I18n.t("message.M058") });
          this.setState({ visible: true });
          this.setState({ type: "error" });
          this.reloadHeaders();
        }
      })
      .catch((err) => console.error("Error: " + err));
  };

  onFinish = (values) => {
    console.log(values.id);
    if (values.id > 0) {
      console.log("onFinish ", values);
      this.onUpdate(values);
    } else {
      this.onSave(values);
      console.log("create", values);
    }
  };

  loadHeaders = () => {
    const url = "/api/v1/header_classification_dicts/index";
    console.log(url);
    fetch(url)
      .then((data) => {
        if (data.ok) {
          return data.json();
        }
        throw new Error("Network error.");
      })
      .then((data) => {
        let count = 0;
        data.forEach((header_classification_dicts) => {
          count++;
          const newEl = {
            key: header_classification_dicts.id,
            id: count,
            display_order: header_classification_dicts.display_order,
            header_classification:
              header_classification_dicts.header_classification,
            keyword: header_classification_dicts.keyword,
          };

          this.setState((prevState) => ({
            header_classification_dicts: [
              ...prevState.header_classification_dicts,
              newEl,
            ],
          }));
        });
      })

      .catch((err) => message.error("Error: " + err));
  };

  render() {
    return (
      <>
        <div
          className="site-layout-background"
          style={{ padding: 24, minHeight: 360 }}
        >
          <Entry
            onFinish={this.onFinish}
            status={this.state.status}
            type={this.state.type}
            visible={this.state.visible}
            state={this.state}
            handleClose={this.handleClose}
            value={this.state.value}
            handleClose={this.state.handleClose}
            editHeader={this.state.editHeader}
            form={this.form}
            key={this.state.key}
            id={this.state.id}
            display_order={this.state.display_order}
            header_classification={this.state.header_classification}
            keyword={this.state.keyword}
          />
          <List
            header_classification_dicts={this.state.header_classification_dicts}
            columns={this.columns}
            deleteStatus={this.deleteStatus}
            handleSearch={this.handleSearch}
            loadHeaders={this.loadHeaders}
            reloadHeaders={this.reloadHeaders}
            state={this.state.header_classification_dicts}
          />
        </div>
      </>
    );
  }
}

export default HeaderClassification;
