import React from "react";
import "antd/dist/antd.css";
import "../../bundles/i18n/ja.js";
import { Form, Input, Button, Card, Alert } from "antd";
import TextArea from "antd/lib/input/TextArea";
import I18n from "i18n-js";

const Entry = (props) => {
  console.log("editheader ", props.editHeader);

  var key = props.key;
  var id = props.id;
  var display_order = props.display_order;
  var header_classification = props.header_classification;
  var keyword = props.keyword;

  const [form] = Form.useForm();

  React.useEffect(() => {
    form.setFieldsValue({
      key: key,
      id: id,
      display_order: display_order,
      header_classification: header_classification,
      keyword: keyword,
    });
  });
  return (
    <Card
      style={{
        width: 1020,
        marginLeft: 25,
        marginRight: 25,
        marginTop: 25,
        paddingBottom: 20,
        paddingLeft: 50,
        height: 390,
      }}
    >
      <div>
        {props.visible ? (
          <Alert
            style={{
              marginBottom: 3,
              textAlign: "center",
              width: 250,
              marginLeft: 270,
            }}
            message={props.status}
            type={props.type}
            closable
            afterClose={props.handleClose}
          />
        ) : null}
      </div>
      <Form
        labelCol={{ span: 5 }}
        wrapperCol={{ span: 8 }}
        style={{ paddingTop: 5, marginLeft: 80 }}
        onFinish={props.onFinish}
        id="form"
        form={form}
      >
        <Form.Item hidden name="key" />
        <Form.Item hidden name="id" />
        <Form.Item
          label="表示順"
          name="display_order"
          rules={[{ required: true, message: I18n.t("message.M013") }]}
        >
          <Input type="number" min="1" />
        </Form.Item>

        <Form.Item
          label="見出し分類"
          name="header_classification"
          rules={[
            {
              required: true,
              message: I18n.t("message.M015"),
            },
          ]}
        >
          <Input />
        </Form.Item>

        <Form.Item
          label="キーワード"
          name="keyword"
          rules={[{ required: true, message: I18n.t("message.M025") }]}
          style={{ resize: "none" }}
        >
          <TextArea rows={5} />
        </Form.Item>

        <Form.Item wrapperCol={{ offset: 8, span: 16 }}>
          <Button type="primary" htmlType="submit" style={{ right: 100 }}>
            保存
          </Button>
        </Form.Item>
      </Form>
    </Card>
  );
};

export default Entry;
