import React, { Component } from "react";
import I18n, { toHumanSize } from "i18n-js";
import "../../bundles/i18n/ja.js";
import {
  Breadcrumb,
  Button,
  Form,
  Input,
  Col,
  Row,
  Alert,
  Dropdown,
  Space,
  Tooltip,
  Select,
} from "antd";
import { DownOutlined, UserOutlined } from "@ant-design/icons";

class groupEntry extends React.Component {
  state = {
    groups: [],
    searchText: "",
    searchedColumn: "",
  };
  formRef = React.createRef();
  //Form ほぞん
  onFinish = (values) => {
    this.props.createGroup(values, this.formRef);
  };

  onFinishFailed = (errorInfo) => {
    console.log("Failed:", errorInfo);
  };

  componentDidMount() {
    this.props.setForm(this.formRef);
  }

  render() {
    const items = [
      {
        label: "1st menu item",
        key: "1",
        icon: <UserOutlined />,
      },
      {
        label: "2nd menu item",
        key: "2",
        icon: <UserOutlined />,
      },
      {
        label: "3rd menu item",
        key: "3",
        icon: <UserOutlined />,
      },
    ];
    const menuProps = {
      items,
      //onClick: handleMenuClick,
    };
    return (
      <>
        <Breadcrumb style={{ margin: "16px 0" }}>
          <Breadcrumb.Item>Home</Breadcrumb.Item>
          <Breadcrumb.Item>Group</Breadcrumb.Item>
        </Breadcrumb>

        <div
          className="site-layout-background"
          style={{ padding: 24, minHeight: 200, background: "#fff" }}
        >
          <Row>
            <Col span={12} offset={6}>
              {this.props.visible ? (
                <Alert
                  message={this.props.message}
                  type={this.props.messageType}
                  closable
                  afterClose={this.props.handleClose}
                  style={{ marginBottom: 10 }}
                  showIcon
                />
              ) : null}

              <Form
                ref={this.formRef}
                name="control-ref"
                onFinish={this.onFinish}
                labelCol={{
                  span: 8,
                }}
              >
                <Form.Item
                  label="グループ名:"
                  name="group_name"
                  rules={[
                    {
                      required: true,
                      message: I18n.t("message.M008"),
                      whitespace: true,
                    },
                  ]}
                >
                  <Input defaultValue={this.props.formData.name} />
                </Form.Item>

                <Form.Item
                  label="グループ概要:"
                  name="group_description"
                  rules={[
                    {
                      required: true,
                      message: I18n.t("message.M010"),
                      whitespace: true,
                    },
                  ]}
                >
                  <Input />
                </Form.Item>

                <Form.Item name="id" hidden={true}>
                  <Input type="text" />
                </Form.Item>

                <Form.Item
                  wrapperCol={{
                    offset: 8,
                    span: 16,
                  }}
                >
                  <Button type="primary" htmlType="submit">
                    保存
                  </Button>
                </Form.Item>
                <Select
                  defaultValue="lucy"
                  style={{
                    width: 120,
                  }}
                  loading
                  options={this.props.dataSource}
                />
              </Form>
            </Col>
          </Row>
        </div>
      </>
    );
  }
}

export default groupEntry;
