import React from "react";
import I18n from "i18n-js";
import "../../bundles/i18n/ja.js";
import Entry from "./_entry.jsx";
import List from "./_list.jsx";
import { Layout, message } from "antd";

class Group extends React.Component {
  state = {
    groups: [],
    searchText: "",
    searchedColumn: "",
    group_name: "",
    group_description: "",
    editGroups: [],
    editStatus: false,
    message: "",
    messageType: "",
    visible: false,
  };

  formRef = React.createRef();

  componentDidMount() {
    this.loadGroups();
  }

  // 取得グループ
  loadGroups = () => {
    const url = "/api/v1/group/index";

    fetch(url)
      .then((data) => {
        if (data.ok) {
          return data.json();
        }
        throw new Error("Network error.");
      })
      .then((data) => {
        data.forEach((group) => {
          const newEl = {
            key: group.id,
            id: group.id,
            name: group.group_name,
            description: group.group_description,
          };

          this.setState((prevState) => ({
            groups: [...prevState.groups, newEl],
          }));
        });
      })
      .catch((err) => message.error("Error: " + err));
  };

  //グループ　更新。
  editGroup = (values, currentForm) => {
    fetch(`/api/v1/group/get/${values.group_name}`)
      .then((data) => {
        if (data.ok) {
          return data.json();
        }
        throw new Error("Network error.");
      })
      .then((data) => {
        var count = Object.keys(data).length;
        if (count > 0) {
          if (data[0].id != values.id) {
            this.setState({
              message: I18n.t("message.M009"),
              messageType: "warning",
              visible: true,
            });
          } else {
            fetch(`/api/v1/group/update/${values.id}`, {
              method: "PUT",
              headers: {
                "Content-Type": "application/json",
              },
              body: JSON.stringify(values),
            })
              .then((data) => {
                if (data.ok) {
                  this.setState({
                    message: I18n.t("message.M005"),
                    messageType: "success",
                    visible: true,
                  });
                  return data.json();
                } else {
                  this.setState({
                    message: I18n.t("message.M058"),
                    messageType: "error",
                    visible: true,
                  });
                }
              })
              .then((data) => {
                let ItemsCopy = [];
                let x = this.state.groups.map((entry) => {
                  if (entry.id == data.id) {
                    entry.name = data.group_name;
                    entry.description = data.group_description;
                  }

                  ItemsCopy.push(entry);
                });

                this.setState({ groups: ItemsCopy });

                this.updateItem(data.id, data);
              });

            currentForm.current.resetFields();
          }
        } else {
          fetch(`/api/v1/group/update/${values.id}`, {
            method: "PUT",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify(values),
          })
            .then((data) => {
              if (data.ok) {
                this.setState({
                  message: I18n.t("message.M005"),
                  messageType: "success",
                  visible: true,
                });
                return data.json();
              } else {
                this.setState({
                  message: I18n.t("message.M058"),
                  messageType: "error",
                  visible: true,
                });
              }
            })
            .then((data) => {
              let ItemsCopy = [];
              let x = this.state.groups.map((entry) => {
                if (entry.id == data.id) {
                  entry.name = data.group_name;
                  entry.description = data.group_description;
                }

                ItemsCopy.push(entry);
              });

              this.setState({ groups: ItemsCopy });

              this.updateItem(data.id, data);
            });

          currentForm.current.resetFields();
        }
      });
  };

  updateItem(id, itemAttributes) {
    var index = this.state.groups.findIndex((x) => x.id === id);

    this.setState({
      groups: [
        ...this.state.groups.slice(0, index),
        Object.assign({}, this.state.groups[index], itemAttributes),
        ...this.state.groups.slice(index + 1),
      ],
    });
  }

  actionNewOrEdit = (values, currentForm) => {
    if (values.id > 0) {
      this.editGroup(values, currentForm);
    } else {
      this.createGroup(values, currentForm);
    }
  };

  //ＤＢ にグループを登録すする
  createGroup = (values, currentForm) => {
    const url = "/api/v1/group/create";

    fetch(`/api/v1/group/get/${values.group_name}`)
      .then((data) => {
        if (data.ok) {
          return data.json();
        }
        throw new Error("Network error.");
      })
      .then((data) => {
        var count = Object.keys(data).length;
        if (count > 0) {
          alert(I18n.t("message.M009"));
        } else {
          fetch(url, {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify(values),
          })
            .then((data) => {
              if (data.ok) {
                return data.json();
              } else {
                alert(I18n.t("message.M056"));
              }
            })
            .then((data) => {
              const newEl = {
                key: data.id,
                id: data.id,
                name: data.group_name,
                description: data.group_description,
              };

              this.setState((prevState) => ({
                groups: [...prevState.groups, newEl],
              }));
              this.setState({
                message: I18n.t("message.M004"),
                messageType: "success",
                visible: true,
              });
            });

          currentForm.current.resetFields();
        }
      })
      .catch((err) => message.error("Error: " + err));
  };

  addNewGroup(group) {
    this.setState({
      groups: this.state.groups.concat(group),
    });
  }

  //更新の削除
  deleteStatus = (e) => {
    const url = "/api/v1/group/destroy";
    fetch(`/api/v1/group/destroy/${e.id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(e),
    }).then((data) => {
      if (data.ok) {
        this.setState({
          message: I18n.t("message.M007"),
          messageType: "success",
          visible: true,
        });

        this.removeGroupFromList(e.id);
        return data.json();
      } else {
        this.setState({
          message: I18n.t("message.M059"),
          messageType: "error",
          visible: true,
        });
      }
    });
  };

  editStatus = (e) => {
    this.formRef.current.setFieldsValue({
      group_name: e.name,
      group_description: e.description,
      id: e.id,
    });

    this.setState({ editStatus: true });
  };

  //登録画面の設定
  setForm = (currentForm) => {
    this.formRef = currentForm;
  };

  //一連で　グループを　削除
  removeGroupFromList(id) {
    const newGroup = this.state.groups.filter((group) => group.id !== id);
    this.setState({
      groups: newGroup,
    });
  }

  handleClose = () => {
    this.setState({ visible: false });
  };

  render() {
    return (
      <Layout style={{ minHeight: "100vh" }}>
        <Entry
          createGroup={this.actionNewOrEdit}
          formData={this.state.editGroups}
          group_name="{this.state.group_name}"
          group_description="{this.state.group_description}"
          setForm={this.setForm}
          message={this.state.message}
          messageType={this.state.messageType}
          visible={this.state.visible}
          handleClose={this.handleClose}
        />
        <List
          dataSource={this.state.groups}
          deleteStatus={this.deleteStatus}
          editStatus={this.editStatus}
        />
      </Layout>
    );
  }
}

export default Group;
