import { Button, Form, Input, Alert, Row, Card, InputNumber, Col } from "antd";
import React from "react";
import "antd/dist/antd.css";
import I18n from "i18n-js";
import "../../bundles/i18n/ja.js";
import PopUpModal from "./_popupModal.jsx";
import FormItem from "antd/lib/form/FormItem";

const { Search } = Input;
const { TextArea } = Input;

const Entry = (props) => {
  var message1 = "";
  const [form] = Form.useForm();

  React.useEffect(() => {
    if (props.state.editable) {
      if (props.state.userId == 0) {
        form.setFieldsValue({
          id: props.editProjectInfo.id,
          key: props.editProjectInfo.key,
          project_name: props.editProjectInfo.project_name,
          min_wordcount: props.editProjectInfo.min_wordcount,
          max_wordcount: props.editProjectInfo.max_wordcount,
          ng_word: props.editProjectInfo.ngWords,
          user_id: props.editProjectInfo.user_id,
          email: props.editProjectInfo.email,
        });
      } else {
        form.setFieldsValue({
          id: props.editProjectInfo.id,
          key: props.editProjectInfo.key,
          project_name: props.editProjectInfo.project_name,
          min_wordcount: props.editProjectInfo.min_wordcount,
          max_wordcount: props.editProjectInfo.max_wordcount,
          ng_word: props.editProjectInfo.ngWords,
          user_id: props.state.userId,
          email: props.state.bEmail,
        });
      }
    } else {
      if (props.state.userId == 0) {
        form.setFieldsValue({
          user_id: props.selectedUser.key,
          email: props.selectedUser.email,
        });
      } else {
        form.setFieldsValue({
          user_id: props.state.userId,
          email: props.state.bEmail,
        });
      }
    }
  });

  return (
    <>
      {props.visible ? (
        <Alert
          className="alert-message-style"
          message={props.status}
          type={props.type}
          closable
          afterClose={props.handleClose}
        />
      ) : null}
      <Form
        onFinish={props.onFinish}
        id="entryForm"
        form={form}
        labelCol={{ span: 10 }}
        wrapperCol={{ span: 6 }}
        layout="horizontal"
      >
        <FormItem name="key" hidden>
          <Input />
        </FormItem>
        <FormItem name="id" hidden>
          <Input />
        </FormItem>
        <FormItem name="user_id" hidden>
          <Input />
        </FormItem>

        <Form.Item
          name="project_name"
          label={[I18n.t("projectInfo.projectName")]}
          rules={[{ required: true, message: I18n.t("message.M019") }]}
        >
          <Input maxLength={255} />
        </Form.Item>

        <Form.Item
          label={[I18n.t("projectInfo.wordcount")]}
          style={{ marginBottom: 0 }}
          required
        >
          <Row>
            <Col span={10}>
              <Form.Item
                name="min_wordcount"
                rules={[
                  { required: true, message: I18n.t("message.M021") },
                  ({ getFieldValue }) => ({
                    validator(_, value) {
                      if (getFieldValue("display_order") < 1) {
                        message1 = I18n.t("message.M001");
                        return Promise.reject();
                      }
                      return Promise.resolve();
                    },
                  }),
                ]}
              >
                <InputNumber
                  formatter={(value) =>
                    `${value}`.replace(/\B(?=(\d{3})+(?!\d))/g, ",")
                  }
                  parser={(value) => value.replace(/\$\s?|(,*)/g, "")}
                  style={{ width: "100%", margin: 0 }}
                  min={1}
                />
              </Form.Item>
            </Col>

            <Col span={4}>
              <span
                style={{
                  display: "inline-block",
                  width: "100%",
                  textAlign: "center",
                  fontSize: 20,
                }}
              >
                ~
              </span>
            </Col>

            <Col span={10}>
              <Form.Item
                name="max_wordcount"
                rules={[
                  { required: true, message: I18n.t("message.M022") },
                  ({ getFieldValue }) => ({
                    validator(_, value) {
                      if (getFieldValue("display_order") < 1) {
                        message1 = I18n.t("message.M001");
                        return Promise.reject();
                      }
                      return Promise.resolve();
                    },
                  }),
                ]}
              >
                <InputNumber
                  formatter={(value) =>
                    `${value}`.replace(/\B(?=(\d{3})+(?!\d))/g, ",")
                  }
                  parser={(value) => value.replace(/\$\s?|(,*)/g, "")}
                  style={{ width: "100%", margin: 0 }}
                  min={1}
                />
              </Form.Item>
            </Col>
          </Row>
        </Form.Item>

        <Form.Item
          name="email"
          label={[I18n.t("projectInfo.email")]}
          style={{ marginTop: 0, paddingTop: 0 }}
          rules={[
            {
              pattern: new RegExp(
                /^[a-zA-Z0-9.!#$%&’*+/=?^_`{|}~-]+@(?:[a-zA-Z0-9]+\.)+[a-zA-Z0-9-]{2,3}$/
              ),
              message: I18n.t("message.M032"),
            },
            {
              required: true,
              message: I18n.t("message.M024"),
            },
          ]}
        >
          <Search
            enterButton
            onSearch={props.showModal}
            onBlur={props.handleBlur}
            maxLength={255}
          />
        </Form.Item>

        <Form.Item name="ng_word" label={[I18n.t("projectInfo.ngWord")]}>
          <TextArea
            style={{ minHeight: 100, resize: "none" }}
            maxLength={255}
          />
        </Form.Item>

        <Form.Item wrapperCol={{ offset: 10, span: 8 }}>
          <Button type="primary" htmlType="submit">
            {I18n.t("common.save")}
          </Button>
        </Form.Item>
      </Form>

      <PopUpModal
        searchResult={props.searchResult}
        modalList={props.modalList}
        modalColumns={props.modalColumns}
        isModalVisible={props.isModalVisible}
        handleCancel={props.handleCancel}
        onSelectChange={props.onSelectChange}
        selectedRowKeys={props.selectedRowKeys}
        email={props.email}
      />
    </>
  );
};
export default Entry;
