import React from 'react';
import I18n from "i18n-js";
import "../../bundles/i18n/ja.js";

import {
    Card,
    Form,
    Input,
    Button,
    Select,
    Table,
    message,
    Popconfirm,
    Space,
} from "antd";
import {
    EditOutlined,
    DeleteOutlined,
    SearchOutlined,
} from "@ant-design/icons";

class NewTemplate extends React.Component{

    tmp = [];

    state = {
        templateTypes: [],
        list: [],
    };

    formFields = {}
   
    layout = {
        labelCol: {
            span: 8,
        },
        wrapperCol: {
            span: 16,
        },
        textalign: "center",
    };

    loadTypes = () => {
        const url = "/api/v1/template_types/index";
        console.log(url);
        fetch(url)
            .then((data) => {
            if (data.ok) {
                return data.json();
            }
            throw new Error("Network error.");
        })
        .then((data) => {
            data.forEach((templates) => { 
                const newEl = { 
                    key: templates.id,
                    id: templates.id,
                    template_type: templates.template_type,
                };

                this.setState((prevState) => ({
                    templateTypes: [...prevState.templateTypes, newEl],
                }));
            });
        })
        //console.log(this.state.templateTypes.template_type)
        .catch((err) => message.error("Error: " + err));
    };

    getTypes = () => {
      
        this.state.templateTypes.forEach((product) => {
            console.log('id ',product.id);
            console.log('name ',product.template_type);
            this.state.list.push(<Select.Option key={product.id} value={product.id}>{product.template_type}</Select.Option>);
        });
    }

    componentDidMount(){
        this.loadTypes();
        // this.getTypes();
    }

    render() {
        return(
        <Card  
        style={
            { 
                width: 1070,
                marginLeft: 25, 
                marginRight: 25, 
                marginTop: 25, 
                paddingBottom: 20, 
                paddingLeft: 50, 
                height: 380 
            }
        } >
        <Form onSubmit={ (e) => { 
            e.preventDefault();
            props.handleFormSubmit(
                formFields.name.value,
                formFields.description.value
            ); 
            e.target.reset();}
            } {...this.layout}  
            >
        <Form.Item
            name="template_type"
            label="テンプレート種類"
            rules={[
                {
                    required: true,
                    message: I18n.t("message.M055"),
                },
            ]}
        >
            <Select　style={{width: 300}}>
                {/* <Select.Option value="choose"></Select.Option>
                <Select.Option value="1">記事の幅</Select.Option>
                <Select.Option value="2">記事の深さ</Select.Option>
                <Select.Option value="3">記事の読みやすさ</Select.Option>
                <Select.Option value="4">見出し詳細</Select.Option> */}
                {/* {this.state.list} */}
                {
                    
                    this.state.templateTypes.forEach((product) => {

                        if(this.tmp.length == 0){
                            this.tmp[0]=product.id;
                            this.state.list.push(<Select.Option key={product.id} value={product.id}>{product.template_type}</Select.Option>);
                        }

                        for(let i=0;i<this.tmp.length;i++){
                            if(this.tmp[i] != product.id){
                                console.log('tmp id',this.tmp[i]);
                                console.log('id ',product.id);
                                //console.log('name ',product.template_type);
                                this.tmp[i]=product.id;
                                this.state.list.push(<Select.Option key={product.id} value={product.id}>{product.template_type}</Select.Option>);
                            }
                        }

                        
                    })
                }
                {this.state.list}
                
            </Select>
        </Form.Item>
        <Form.Item
        name="display_order"
        label="表示順"
        rules={[
          {
            required: true,
            message: I18n.t("message.M013"),
          },
        ]}
        >
            <Input style={{width: 300}} />
        </Form.Item>

        <Form.Item
        name="template_name"
        label="テンプレート名称"
        rules={[
          {
            required: true,
            message: I18n.t("message.M035"),
          },
        ]}
        >
            <Input style={{width: 300}} />
        </Form.Item>
      
        <Form.Item 
        name="description"
        label="テンプレート内容" 
        rules={[
          {
            required: true,
            message: I18n.t("message.M037"),
          },
        ]}>
            <Input.TextArea style={{width: 300, resize: "none", minHeight: 100 }} />
        </Form.Item>
        <Form.Item wrapperCol={{ ...this.layout.wrapperCol, offset: 8 }}>
            <Button type="primary" htmlType="submit">
                保存
            </Button>
        </Form.Item>
    </Form>
    </Card>
    )
    }
    
  }
export default NewTemplate;
